LSession.shared.dataTask(with: url) { data, _, error in            if let error = error {                completion(.failure(error))                return            }            guard let data = data else {                completion(.failure(NSError(domain: "Sem dados", code: 0)))                return            }            do {                var decoded = try JSONDecoder().decode(Pokemon.self, from: data)                let imageURL = "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/\(decoded.id).png"                decoded.imageURL = imageURL                completion(.success(decoded))            } catch {                completion(.failure(error))            }        }.resume()    }}